pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title UniversalStaking
 * @dev Contrat de staking universel pour n'importe quel token ERC20
 * Chaque token a sa propre pool avec ses paramètres personnalisables
 */
contract UniversalStaking is Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    struct StakeInfo {
        uint256 amount;        // Montant staké
        uint256 stakeTime;     // Timestamp du stake
        uint256 lastRewardTime; // Dernier calcul de rewards
    }

    struct PoolInfo {
        IERC20 token;          // Token à staker
        uint256 rewardRate;    // Reward rate (tokens par seconde par token staké)
        uint256 totalStaked;   // Total staké dans cette pool
        uint256 lastUpdateTime;// Dernière mise à jour
        bool isActive;         // Pool active ou non
        uint256 minStakeAmount; // Montant minimum à staker
        uint256 lockPeriod;    // Période de lock en secondes (0 = pas de lock)
    }

    // Mapping des pools par adresse de token
    mapping(address => PoolInfo) public pools;

    // Mapping des stakes: token => user => stake info
    mapping(address => mapping(address => StakeInfo)) public stakes;

    // Liste des tokens supportés
    address[] public supportedTokens;

    // Events
    event PoolCreated(address indexed token, uint256 rewardRate, uint256 minStakeAmount, uint256 lockPeriod);
    event Staked(address indexed user, address indexed token, uint256 amount);
    event Unstaked(address indexed user, address indexed token, uint256 amount);
    event RewardsClaimed(address indexed user, address indexed token, uint256 reward);
    event PoolUpdated(address indexed token, uint256 newRewardRate);

    constructor() Ownable(msg.sender) {}

    /**
     * @dev Créer une nouvelle pool de staking pour un token
     */
    function createPool(
        address _token,
        uint256 _rewardRate,
        uint256 _minStakeAmount,
        uint256 _lockPeriod
    ) external onlyOwner {
        require(_token != address(0), "Invalid token address");
        require(!pools[_token].isActive, "Pool already exists");

        pools[_token] = PoolInfo({
            token: IERC20(_token),
            rewardRate: _rewardRate,
            totalStaked: 0,
            lastUpdateTime: block.timestamp,
            isActive: true,
            minStakeAmount: _minStakeAmount,
            lockPeriod: _lockPeriod
        });

        supportedTokens.push(_token);
        emit PoolCreated(_token, _rewardRate, _minStakeAmount, _lockPeriod);
    }

    /**
     * @dev Staker des tokens
     */
    function stake(address _token, uint256 _amount) external nonReentrant {
        require(pools[_token].isActive, "Pool not active");
        require(_amount >= pools[_token].minStakeAmount, "Amount below minimum");

        PoolInfo storage pool = pools[_token];
        StakeInfo storage userStake = stakes[_token][msg.sender];

        // Transfer tokens to contract
        pool.token.safeTransferFrom(msg.sender, address(this), _amount);

        // Update pool
        pool.totalStaked += _amount;
        pool.lastUpdateTime = block.timestamp;

        // Update user stake
        if (userStake.amount > 0) {
            // Si l'utilisateur avait déjà des tokens stakés, on calcule ses rewards
            userStake.amount += _amount;
        } else {
            // Premier stake
            userStake.amount = _amount;
            userStake.stakeTime = block.timestamp;
        }
        userStake.lastRewardTime = block.timestamp;

        emit Staked(msg.sender, _token, _amount);
    }

    /**
     * @dev Unstaker des tokens
     */
    function unstake(address _token, uint256 _amount) external nonReentrant {
        require(pools[_token].isActive, "Pool not active");

        StakeInfo storage userStake = stakes[_token][msg.sender];
        require(userStake.amount >= _amount, "Insufficient staked amount");

        // Vérifier la période de lock
        PoolInfo storage pool = pools[_token];
        if (pool.lockPeriod > 0) {
            require(
                block.timestamp >= userStake.stakeTime + pool.lockPeriod,
                "Tokens still locked"
            );
        }

        // Update pool
        pool.totalStaked -= _amount;
        pool.lastUpdateTime = block.timestamp;

        // Update user stake
        userStake.amount -= _amount;
        if (userStake.amount == 0) {
            userStake.stakeTime = 0;
            userStake.lastRewardTime = 0;
        }

        // Transfer tokens back to user
        pool.token.safeTransfer(msg.sender, _amount);

        emit Unstaked(msg.sender, _token, _amount);
    }

    /**
     * @dev Calculer les rewards d'un utilisateur pour un token
     */
    function calculateRewards(address _token, address _user) public view returns (uint256) {
        if (!pools[_token].isActive) return 0;

        StakeInfo memory userStake = stakes[_token][_user];
        if (userStake.amount == 0) return 0;

        PoolInfo memory pool = pools[_token];

        // Temps écoulé depuis le dernier calcul de reward
        uint256 timeElapsed = block.timestamp - userStake.lastRewardTime;

        // Reward = montant staké * reward rate * temps écoulé
        return (userStake.amount * pool.rewardRate * timeElapsed) / 1e18;
    }

    /**
     * @dev Claim rewards (simulation - dans un vrai contrat il faudrait une réserve de tokens)
     */
    function claimRewards(address _token) external nonReentrant {
        require(pools[_token].isActive, "Pool not active");

        uint256 reward = calculateRewards(_token, msg.sender);
        require(reward > 0, "No rewards to claim");

        // Mettre à jour le temps du dernier reward
        stakes[_token][msg.sender].lastRewardTime = block.timestamp;

        // Dans un vrai contrat, on transférerait les tokens de reward
        // Pour la démo, on émet juste l'événement
        emit RewardsClaimed(msg.sender, _token, reward);
    }

    /**
     * @dev Obtenir les informations d'une pool
     */
    function getPoolInfo(address _token) external view returns (
        uint256 rewardRate,
        uint256 totalStaked,
        uint256 minStakeAmount,
        uint256 lockPeriod,
        bool isActive
    ) {
        PoolInfo memory pool = pools[_token];
        return (
            pool.rewardRate,
            pool.totalStaked,
            pool.minStakeAmount,
            pool.lockPeriod,
            pool.isActive
        );
    }

    /**
     * @dev Obtenir les informations de stake d'un utilisateur
     */
    function getUserStakeInfo(address _token, address _user) external view returns (
        uint256 stakedAmount,
        uint256 stakeTime,
        uint256 pendingRewards,
        bool canUnstake
    ) {
        StakeInfo memory userStake = stakes[_token][_user];
        PoolInfo memory pool = pools[_token];

        bool canUnstakeNow = true;
        if (pool.lockPeriod > 0 && userStake.stakeTime > 0) {
            canUnstakeNow = block.timestamp >= userStake.stakeTime + pool.lockPeriod;
        }

        return (
            userStake.amount,
            userStake.stakeTime,
            calculateRewards(_token, _user),
            canUnstakeNow
        );
    }

    /**
     * @dev Mettre à jour le reward rate d'une pool
     */
    function updateRewardRate(address _token, uint256 _newRewardRate) external onlyOwner {
        require(pools[_token].isActive, "Pool not active");
        pools[_token].rewardRate = _newRewardRate;
        emit PoolUpdated(_token, _newRewardRate);
    }

    /**
     * @dev Obtenir la liste des tokens supportés
     */
    function getSupportedTokens() external view returns (address[] memory) {
        return supportedTokens;
    }

    /**
     * @dev Emergency function to recover stuck tokens (only owner)
     */
    function emergencyWithdraw(address _token, uint256 _amount) external onlyOwner {
        IERC20(_token).safeTransfer(owner(), _amount);
    }
}","keccak256":"0x974c5d3a54f76599000f0cb8986ba3bf99f0ecc6927bf01ba9fd3dd0702184aa","license":"MIT"}},"version":1}
